const https = require\(''https''\);
const fs = require\(''fs''\);

// Read the vercel config
const configPath = require\(''path''\).join\(require\(''os''\).homedir\(\), ''.config/vercel/auth.json''\);
let token;
try {
  const config = JSON.parse\(fs.readFileSync\(configPath, ''utf8''\)\);
  token = config.token;
  console.log\(''Found Vercel token''\);
} catch \(e\) {
  // Try Windows path
  const winPath = require\(''path''\).join\(require\(''os''\).homedir\(\), ''AppData/Roaming/com.vercel.cli/auth.json''\);
  try {
    const config = JSON.parse\(fs.readFileSync\(winPath, ''utf8''\)\);
    token = config.token;
    console.log\(''Found Vercel token \(Windows\)''\);
  } catch \(e2\) {
    console.log\(''Could not find Vercel token''\);
  }
}
console.log\(''Token available:'', !!token\);
")

